mapper.insert:
insert into shop_product_category(parent_id, name, level, nav_status, sort, icon)
    value (
           #{parentId}, #{name},
           #{level}, #{navStatus},
           #{sort}, #{icon}
    )

mapper.update:
update shop_product_category
set parent_id = #{parentId}

    ,name = #{name}


    ,icon = #{icon}


    ,level = #{level}


    ,nav_status = #{navStatus}


    ,sort = #{sort}

where id = #{id}

mapper.select:
select id,
       parent_id,
       name
from shop_product_category
where parent_id = #{parentId}
  and name = #{name}

select id, parent_id, name, level, nav_status, sort, icon from shop_product_category where id in (
#{id}
)
